// src/pages/WhiteboardLabelingPage.js
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Typography, Button } from "@mui/material";
import { Stage, Layer, Image as KonvaImage, Rect } from "react-konva";
import useImage from "use-image"; // Hook for loading images

const whiteboards = [
  {
    id: "wb_456",
    imageUrl: "https://via.placeholder.com/400",
    annotated: true,
  },
  {
    id: "wb_457",
    imageUrl: "https://via.placeholder.com/400",
    annotated: false,
  },
  // Add more whiteboards as needed
];

function WhiteboardLabelingPage() {
  const { id } = useParams(); // Get whiteboard ID from URL
  const whiteboard = whiteboards.find((wb) => wb.id === id);
  const [image] = useImage(whiteboard ? whiteboard.imageUrl : "");

  // Annotation state
  const [annotations, setAnnotations] = useState([]);
  const [newAnnotation, setNewAnnotation] = useState(null);

  // Add a new annotation rectangle
  const handleMouseDown = (e) => {
    const { x, y } = e.target.getStage().getPointerPosition();
    setNewAnnotation({ x, y, width: 0, height: 0 });
  };

  const handleMouseMove = (e) => {
    if (!newAnnotation) return;
    const stage = e.target.getStage();
    const pointerPosition = stage.getPointerPosition();
    setNewAnnotation({
      ...newAnnotation,
      width: pointerPosition.x - newAnnotation.x,
      height: pointerPosition.y - newAnnotation.y,
    });
  };

  const handleMouseUp = () => {
    if (newAnnotation) {
      setAnnotations([...annotations, newAnnotation]);
      setNewAnnotation(null);
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Whiteboard ID: {id}
      </Typography>
      {whiteboard ? (
        <Stage
          width={500}
          height={400}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
        >
          <Layer>
            <KonvaImage image={image} width={500} height={400} />
            {annotations.map((rect, i) => (
              <Rect
                key={i}
                x={rect.x}
                y={rect.y}
                width={rect.width}
                height={rect.height}
                fill="rgba(255,165,0,0.5)"
              />
            ))}
            {newAnnotation && (
              <Rect
                x={newAnnotation.x}
                y={newAnnotation.y}
                width={newAnnotation.width}
                height={newAnnotation.height}
                fill="rgba(0,0,255,0.5)"
              />
            )}
          </Layer>
        </Stage>
      ) : (
        <Typography color="error">Whiteboard not found</Typography>
      )}
      <Button
        variant="contained"
        color="primary"
        style={{ marginTop: "20px" }}
        onClick={() => console.log("Annotations:", annotations)}
      >
        Save Annotations
      </Button>
    </Container>
  );
}

export default WhiteboardLabelingPage;
