// src/pages/WhiteboardLabelingPage.js
import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import {
  Container,
  Typography,
  IconButton,
  TextField,
  Paper,
  Button,
} from "@mui/material";
import { ArrowBack } from "@mui/icons-material";
import { Stage, Layer, Image as KonvaImage, Rect } from "react-konva";
import useImage from "use-image";

const whiteboards = [
  {
    id: "wb_456",
    imageUrl: "https://via.placeholder.com/1200x800",
    annotated: true,
  },
  {
    id: "wb_457",
    imageUrl: "https://via.placeholder.com/1200x800",
    annotated: false,
  },
  {
    id: "wb_458",
    imageUrl: "https://via.placeholder.com/1200x800",
    annotated: false,
  },
];

const confidenceColors = [
  "rgba(0, 255, 0, 0.5)", // High Confidence (Green)
  "rgba(255, 165, 0, 0.5)", // Medium Confidence (Orange)
  "rgba(255, 0, 0, 0.5)", // Low Confidence (Red)
];

function WhiteboardLabelingPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const whiteboard = whiteboards.find((wb) => wb.id === id);
  const [image] = useImage(whiteboard ? whiteboard.imageUrl : "");

  const [annotations, setAnnotations] = useState([]);
  const [newAnnotation, setNewAnnotation] = useState(null);
  const [activeAnnotation, setActiveAnnotation] = useState(null);
  const [transcription, setTranscription] = useState("");
  const [panelWidth, setPanelWidth] = useState(70);

  const handleMouseDown = (e) => {
    const { x, y } = e.target.getStage().getPointerPosition();
    setNewAnnotation({
      x,
      y,
      width: 0,
      height: 0,
      confidenceLevel: 0,
      transcription: "",
    });
  };

  const handleMouseMove = (e) => {
    if (!newAnnotation) return;
    const stage = e.target.getStage();
    const pointerPosition = stage.getPointerPosition();
    setNewAnnotation({
      ...newAnnotation,
      width: pointerPosition.x - newAnnotation.x,
      height: pointerPosition.y - newAnnotation.y,
    });
  };

  const handleMouseUp = () => {
    if (newAnnotation) {
      // Add the new annotation to the list
      setAnnotations((prevAnnotations) => {
        const updatedAnnotations = [...prevAnnotations, newAnnotation];
        return updatedAnnotations;
      });

      // Set the new annotation as active and open the transcription editor
      setActiveAnnotation(annotations.length); // Use the new annotation as active
      setTranscription(newAnnotation.transcription); // Load its transcription
      setNewAnnotation(null); // Reset the drawing state
    }
  };

  const handleAnnotationClick = (index) => {
    // Cycle the confidence level color (0 -> 1 -> 2 -> 0)
    const updatedAnnotations = [...annotations];
    const currentAnnotation = updatedAnnotations[index];

    // Increment the confidence level (cycling through 0, 1, 2)
    const nextConfidenceLevel =
      (currentAnnotation.confidenceLevel + 1) % confidenceColors.length;

    // Update the annotation's confidence level
    updatedAnnotations[index] = {
      ...currentAnnotation,
      confidenceLevel: nextConfidenceLevel,
    };
    setAnnotations(updatedAnnotations);

    // Set active annotation to open the transcription editor
    setActiveAnnotation(index);
    setTranscription(updatedAnnotations[index].transcription);
  };

  const handleTranscriptionChange = (event) => {
    const updatedTranscription = event.target.value;
    setTranscription(updatedTranscription);

    // Auto-save the transcription when it changes
    setAnnotations((prevAnnotations) =>
      prevAnnotations.map((annotation, i) =>
        i === activeAnnotation
          ? { ...annotation, transcription: updatedTranscription }
          : annotation
      )
    );
  };

  const handleResize = (e) => {
    const newWidth = (e.clientX / window.innerWidth) * 100;
    setPanelWidth(Math.max(30, Math.min(newWidth, 85)));
  };

  // Handlers for navigating whiteboards
  const handleNext = () => {
    const currentIndex = whiteboards.findIndex((wb) => wb.id === id);
    const nextIndex = (currentIndex + 1) % whiteboards.length;
    navigate(`/label/${whiteboards[nextIndex].id}`);
  };

  const handlePrevious = () => {
    const currentIndex = whiteboards.findIndex((wb) => wb.id === id);
    const prevIndex =
      (currentIndex - 1 + whiteboards.length) % whiteboards.length;
    navigate(`/label/${whiteboards[prevIndex].id}`);
  };

  return (
    <Container
      maxWidth={false}
      style={{ height: "100vh", display: "flex", padding: 0 }}
    >
      {/* Whiteboard Section */}
      <div
        style={{
          width: `${panelWidth}%`,
          overflowY: "auto",
          position: "relative",
        }}
      >
        <IconButton
          onClick={() => navigate("/whiteboards")}
          style={{
            position: "absolute",
            top: "20px",
            left: "20px",
            zIndex: 1000,
          }}
        >
          <ArrowBack fontSize="large" />
        </IconButton>
        <Typography
          variant="h6"
          style={{
            position: "absolute",
            top: "20px",
            left: "50%",
            transform: "translateX(-50%)",
            zIndex: 1000,
            opacity: 0.8,
            backgroundColor: "white",
            padding: "5px 15px",
            borderRadius: "4px",
          }}
        >
          Whiteboard ID: {id}
        </Typography>
        {whiteboard ? (
          <Stage
            width={1200}
            height={800}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={{ margin: "0 auto" }}
          >
            <Layer>
              <KonvaImage image={image} width={1200} height={800} />
              {annotations.map((rect, i) => (
                <Rect
                  key={i}
                  x={rect.x}
                  y={rect.y}
                  width={rect.width}
                  height={rect.height}
                  fill={confidenceColors[rect.confidenceLevel]} // Set color based on confidence level
                  onClick={() => handleAnnotationClick(i)}
                />
              ))}
              {newAnnotation && (
                <Rect
                  x={newAnnotation.x}
                  y={newAnnotation.y}
                  width={newAnnotation.width}
                  height={newAnnotation.height}
                  fill="rgba(0,0,255,0.5)"
                />
              )}
            </Layer>
          </Stage>
        ) : (
          <Typography color="error">Whiteboard not found</Typography>
        )}

        {/* Previous and Next Buttons */}
        <Button
          variant="contained"
          color="secondary"
          onClick={handlePrevious}
          style={{
            position: "fixed",
            bottom: "20px",
            left: "20px",
            opacity: 0.7,
          }}
        >
          Previous Whiteboard
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={handleNext}
          style={{
            position: "fixed",
            bottom: "20px",
            right: "20px",
            opacity: 0.7,
          }}
        >
          Next Whiteboard
        </Button>
      </div>

      {/* Divider for Resizing */}
      <div
        onMouseDown={(e) => {
          document.addEventListener("mousemove", handleResize);
          document.addEventListener("mouseup", () => {
            document.removeEventListener("mousemove", handleResize);
          });
        }}
        style={{
          width: "5px",
          cursor: "col-resize",
          backgroundColor: "#ccc",
        }}
      />

      {/* Transcription Panel */}
      {activeAnnotation !== null && (
        <Paper
          style={{
            width: `${100 - panelWidth}%`,
            padding: "20px",
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
            boxShadow: "0px 4px 12px rgba(0,0,0,0.2)",
            overflowY: "auto",
          }}
          square
        >
          <Typography variant="h6" gutterBottom>
            Edit Transcription
          </Typography>
          <TextField
            fullWidth
            label="Transcription"
            value={annotations[activeAnnotation]?.transcription || ""}
            onChange={handleTranscriptionChange}
            multiline
            rows={4}
            variant="outlined"
          />
        </Paper>
      )}
    </Container>
  );
}

export default WhiteboardLabelingPage;
